import React from "react";
import { useNavigate } from "react-router-dom";

export default function Wishlist({ wishlist, toggleWishlist }) {
  const navigate = useNavigate();

  return (
    <div className="wishlist-page">
      <header className="header">
        <h1>❤️ My Wishlist</h1>
        <button className="back-btn" onClick={() => navigate("/")}>
          ⬅️ Back to Dashboard
        </button>
      </header>

      {wishlist.length === 0 ? (
        <p className="empty-msg">Your wishlist is empty. Add some remedies!</p>
      ) : (
        <div className="wishlist-grid">
          {wishlist.map((item, index) => (
            <div key={index} className="wishlist-card">
              <h3>{item.title}</h3>
              <p>{item.details}</p>
              <button className="remove-btn" onClick={() => toggleWishlist(item)}>
                ❌ Remove
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
