import React, { useState } from 'react'; 
import { useNavigate } from 'react-router-dom'; 
import './LoginPage.css';  // ‚úÖ We'll create this CSS next

function LoginPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate(); 

  const handleSubmit = (event) => {
    event.preventDefault(); 

    if (username.trim() === '' || password.trim() === '') {
      alert('‚ö†Ô∏è Please enter both username and password.');
      return;
    }

    // ‚úÖ Allow anyone to log in
    alert(`‚úÖ Welcome, ${username}! You‚Äôve successfully logged in.`);
    navigate('/dashboard'); 
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <h1 className="login-title">üåø Nature‚Äôs Cure</h1>
        <p className="subtitle">Login to explore natural remedies</p>

        <form onSubmit={handleSubmit} className="login-form">
          <div className="input-group">
            <label htmlFor="username">Username</label>
            <input 
              type="text" 
              id="username" 
              placeholder="Enter username"
              value={username} 
              onChange={(e) => setUsername(e.target.value)} 
            />
          </div>

          <div className="input-group">
            <label htmlFor="password">Password</label>
            <input 
              type="password" 
              id="password" 
              placeholder="Enter password"
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
            />
          </div>

          <button type="submit" className="login-button">Login</button>

          <p className="extra-links">
            <a href="#">Forgot Password?</a> | <a href="#">Sign Up</a>
          </p>
        </form>
      </div>
    </div>
  );
}

export default LoginPage;
