import React, { useState } from "react";


export default function Dashboard() {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");

  const categories = [
    {
      title: "Skin Care",
      image:
        "https://images.unsplash.com/photo-1506003094589-53954a26283f?ixlib=rb-4.1.0&auto=format&fit=crop&w=800&q=80",
      description: "Natural ways to keep your skin glowing and healthy.",
      remedies: [
        { title: "Aloe Vera Gel", details: "Apply fresh aloe vera gel daily to soothe and hydrate your skin." },
        { title: "Turmeric Mask", details: "Mix turmeric with honey and yogurt for glowing, blemish-free skin." },
        { title: "Cucumber Toner", details: "Blend cucumber, strain, and use the juice as a toner to cool and refresh your skin naturally." },
        { title: "Multani Mitti Mask", details: "Mix Multani Mitti with rose water for oil control and acne reduction." },
        { title: "Rose Water Mist", details: "Spray rose water throughout the day for instant hydration." },
      ],
    },
    {
      title: "Home Remedies",
      image:
        "https://tse1.mm.bing.net/th/id/OIP.zUKD2bpw7dN4nNjrTSQYYAHaEK?pid=Api&P=0&h=180",
      description: "Simple remedies for everyday ailments using household ingredients.",
      remedies: [
        { title: "Honey for Cough", details: "Take a spoonful of honey to soothe sore throats and reduce coughing." },
        { title: "Jeera Water", details: "Boil cumin seeds and drink daily for better digestion." },
        { title: "Ginger Tea", details: "Drink ginger tea twice a day for better metabolism and digestion." },
      ],
    },
    {
      title: "Weight Loss",
      image:
        "https://tse4.mm.bing.net/th/id/OIP.yo6pqD54F7u8KqcQMs9FGwHaE8?pid=Api&P=0&h=180",
      description: "Natural and effective home remedies to burn fat and stay fit.",
      remedies: [
        { title: "Lemon & Honey Water", details: "Drink warm lemon honey water in the morning to detoxify your body." },
        { title: "Green Tea", details: "Drink 2-3 cups daily to boost metabolism." },
        { title: "Apple Cider Vinegar", details: "Take a tablespoon before meals to control appetite." },
      ],
    },
    {
      title: "Weight Gain",
      image:
        "https://tse1.mm.bing.net/th/id/OIF.00DMlAJv69UmKgqazpX8zg?pid=Api&P=0&h=180",
      description: "Boost your weight naturally and gain healthy with these remedies.",
      remedies: [
        { title: "Banana Milkshake", details: "Blend bananas and milk for a calorie-rich drink." },
        { title: "Dry Fruits & Nuts", details: "Eat almonds and raisins daily for healthy fat gain." },
      ],
    },
    {
      title: "Beauty Care",
      image:
        "https://images.unsplash.com/photo-1636499287985-85871c3406fc?ixlib=rb-4.1.0&auto=format&fit=crop&w=800&q=80",
      description: "Enhance your beauty with herbal and organic care tips.",
      remedies: [
        { title: "Rose Water Toner", details: "Use rose water to tighten pores and refresh your skin." },
        { title: "Honey & Lemon Pack", details: "Brighten your skin naturally with honey and lemon juice." },
      ],
    },
    {
      title: "Health Care",
      image:
        "https://media.istockphoto.com/id/812997516/photo/selection-of-healthy-rich-fiber-sources-vegan-food-for-cooking.webp?a=1&b=1&s=612x612&w=0&k=20&c=0oekyfvRbQTbpHoomcyqGTbyA-ps4zrMzyVX6cvLz2U=",
      description: "Improve your overall health through natural practices.",
      remedies: [
        { title: "Turmeric Milk", details: "Drink warm turmeric milk to boost your immunity." },
        { title: "Tulsi Leaves", details: "Chew tulsi leaves daily for stronger immunity." },
      ],
    },
  ];

  // âœ… Filter categories & remedies based on search term
  const filteredCategories = categories
    .map((cat) => ({
      ...cat,
      remedies: cat.remedies.filter(
        (rem) =>
          rem.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
          rem.details.toLowerCase().includes(searchTerm.toLowerCase())
      ),
    }))
    .filter(
      (cat) =>
        cat.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        cat.remedies.length > 0
    );

  const handleExplore = (category) => {
    setSelectedCategory(selectedCategory === category.title ? null : category.title);
  };

  return (
    <div className="dashboard">
      <div className="overlay">
        <header className="header">
          <h1>ðŸŒ¿ Natureâ€™s Cure</h1>
          <p>Discover natural remedies for beauty and health.</p>

          {/* âœ… Search Bar */}
          <div className="search-bar">
            <input
              type="text"
              placeholder="ðŸ” Search remedies or categories..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </header>

        {/* âœ… Cards Display */}
        <div className="card-grid">
          {filteredCategories.length > 0 ? (
            filteredCategories.map((item, index) => (
              <div key={index} className="card">
                <img src={item.image} alt={item.title} className="card-image" />
                <h2>{item.title}</h2>
                <p>{item.description}</p>

                <button className="explore-btn" onClick={() => handleExplore(item)}>
                  {selectedCategory === item.title ? "Hide Remedies" : "Explore"}
                </button>

                {selectedCategory === item.title && (
                  <div className="remedy-container">
                    {item.remedies.map((rem, i) => (
                      <div key={i} className="remedy-card">
                        <h3>{rem.title}</h3>
                        <p>{rem.details}</p>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            ))
          ) : (
            <p className="no-results">No results found for "{searchTerm}" ðŸ˜•</p>
          )}
        </div>
      </div>
    </div>
  );
}
