import React, { useState } from "react";

export default function Dashboard() {
  const [selectedCategory, setSelectedCategory] = useState(null);

  // ðŸŒ¿ Categories and Remedies Data
  const categories = [
    {
      title: "Skin Care",
      image:
        "https://images.unsplash.com/photo-1506003094589-53954a26283f?ixlib=rb-4.1.0&auto=format&fit=crop&q=60&w=600",
      description: "Natural ways to keep your skin glowing and healthy.",
      remedies: [
        { title: "Aloe Vera Gel", details: "Apply fresh aloe vera gel daily to soothe and hydrate your skin." },
        { title: "Turmeric Mask", details: "Mix turmeric with honey and yogurt for glowing, blemish-free skin." },
        { title: "Coconut Oil Massage", details: "Massage with virgin coconut oil to maintain moisture and softness." },
      ],
    },
    {
      title: "Beauty Care",
      image:
        "https://images.unsplash.com/photo-1636499287985-85871c3406fc?ixlib=rb-4.1.0&auto=format&fit=crop&q=60&w=600",
      description: "Enhance your beauty with herbal and organic care tips.",
      remedies: [
        { title: "Rose Water Toner", details: "Use rose water as a natural toner to tighten pores and refresh your skin." },
        { title: "Honey & Lemon Pack", details: "Brighten your skin naturally by mixing honey and lemon juice." },
        { title: "Aloe Vera Moisturizer", details: "Apply fresh aloe vera gel to hydrate your skin and reduce acne marks." },
      ],
    },
    {
      title: "Health Care",
      image:
        "https://tse4.mm.bing.net/th/id/OIP.X6l-8Q5xIdOuBbb7I1zkMQHaEO?pid=Api&P=0&h=180",
      description: "Improve your overall health through natural practices.",
      remedies: [
        { title: "Turmeric Milk", details: "Drink a glass of warm milk with turmeric before bed to boost immunity." },
        { title: "Tulsi Leaves", details: "Chew a few tulsi leaves daily to strengthen your immunity." },
        { title: "Ginger Tea", details: "Drink ginger tea daily to boost digestion and immunity." },
      ],
    },
  ];

  // ðŸ”˜ When user clicks "Explore"
  const handleExplore = (category) => {
    setSelectedCategory(selectedCategory === category.title ? null : category.title);
  };

  return (
    <div style={styles.background}>
      <div style={styles.overlay}>
        <header style={styles.header}>
          <h1>ðŸŒ¿ Natureâ€™s Cure</h1>
          <p>Explore natural ways to heal and rejuvenate your body and soul.</p>
        </header>

        <div style={styles.grid}>
          {categories.map((item, index) => (
            <div key={index} style={styles.card}>
              <img src={item.image} alt={item.title} style={styles.image} />
              <h2>{item.title}</h2>
              <p>{item.description}</p>

              <button style={styles.button} onClick={() => handleExplore(item)}>
                {selectedCategory === item.title ? "Hide Remedies" : "Explore"}
              </button>

              {selectedCategory === item.title && (
                <div style={styles.remedyContainer}>
                  {item.remedies.map((rem, i) => (
                    <div key={i} style={styles.remedyCard}>
                      <h3>{rem.title}</h3>
                      <p>{rem.details}</p>
                    </div>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

// ðŸŽ¨ Styling
const styles = {
  background: {
    backgroundImage:
      "url('https://images.unsplash.com/photo-1501004318641-b39e6451bec6?auto=format&fit=crop&w=1500&q=80')", // ðŸŒ¿ natural background
    backgroundSize: "cover",
    backgroundPosition: "center",
    minHeight: "100vh",
    width: "100%",
  },
  overlay: {
    backgroundColor: "rgba(255, 255, 255, 0.85)", // slight white overlay for readability
    minHeight: "100vh",
    padding: "30px",
    textAlign: "center",
  },
  header: {
    marginBottom: "30px",
  },
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
    gap: "20px",
  },
  card: {
    backgroundColor: "rgba(255, 255, 255, 0.9)",
    borderRadius: "16px",
    boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
    padding: "20px",
    transition: "0.3s",
  },
  image: {
    width: "100%",
    height: "180px",
    borderRadius: "12px",
    objectFit: "cover",
  },
  button: {
    backgroundColor: "#4CAF50",
    border: "none",
    borderRadius: "8px",
    color: "#fff",
    padding: "10px 20px",
    marginTop: "10px",
    cursor: "pointer",
    fontWeight: "bold",
  },
  remedyContainer: {
    marginTop: "15px",
    backgroundColor: "#f0f8e9",
    borderRadius: "10px",
    padding: "10px",
  },
  remedyCard: {
    backgroundColor: "#fff",
    margin: "10px 0",
    borderRadius: "8px",
    padding: "10px",
    boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
    textAlign: "left",
  },
};
