import { Link, useLocation } from "react-router-dom";
import { Navbar, Nav, Container } from "react-bootstrap";
import logo from "../Images/Logo.png";

function Header() {
  const location = useLocation();
  const isDashboard = location.pathname === "/dashboard";

  return (
    <>
      {/* ✅ Internal CSS override */}
      <style>{`
        .dashboard-nav {
          position: static !important;
          box-shadow: none !important;
          padding-top: 0 !important;
        }
      `}</style>

      {/* ✅ Add dashboard-nav dynamically, but no code removed or changed */}
      <Navbar
        expand="lg"
        bg="light"
        className={`custom-navbar shadow-sm ${isDashboard ? "dashboard-nav" : ""}`}
      >
        <Container>
          {/* Logo + Brand */}
          <Navbar.Brand as={Link} to="/" className="navbar-brand-custom">
            <img
              src={logo}
              alt="Nature's Cure"
              className="navbar-logo"
            />
            <span className="brand-text">Nature's Cure</span>
          </Navbar.Brand>

          <Navbar.Toggle aria-controls="navbarScroll" />

          <Navbar.Collapse id="navbarScroll">
            {/* Home & About (next to brand) */}
            <Nav className="brand-links ms-4">
              <Nav.Link as={Link} to="/" className="nav-link-custom">
                Home
              </Nav.Link>
              <Nav.Link as={Link} to="/about" className="nav-link-custom">
                About
              </Nav.Link>
            </Nav>

            {/* Login on right */}
            <Nav className="ms-auto">
              <Nav.Link as={Link} to="/login" className="login-link">
                Login
              </Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </>
  );
}

export default Header;
