import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { categories } from "./data"; // make sure your data is imported

export default function Dashboard() {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const navigate = useNavigate();

  // When user clicks Explore â†’ Go to CategoryPage
  const handleExplore = (category) => {
    navigate(`/category/${category.title}`, { state: { category } }); 
    // âœ… sends the whole category object to next page
  };

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>ðŸŒ¿ Natureâ€™s Cure</h1>
        <p>Explore natural ways to heal and rejuvenate your body and soul.</p>
      </header>

      <div className="card-grid">
        {categories.map((item, index) => (
          <div key={index} className="card">
            <img src={item.image} alt={item.title} className="card-image" />
            <h2>{item.title}</h2>
            <p>{item.description}</p>

            {/* Explore Button */}
            <button
              className="explore-btn"
              onClick={() => handleExplore(item)} // âœ… Pass the entire item (object)
            >
              Explore
            </button>

            {/* Optional: keep this if you still want to show remedies inline */}
            {selectedCategory === item.title && (
              <div className="remedies">
                {item.remedies.map((rem, i) => (
                  <div key={i} className="remedy-card">
                    <h3>{rem.title}</h3>
                    <p>{rem.details}</p>
                  </div>
                ))}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
