import React, { useState } from "react";

const categories = [
  {
    title: "Skin Care",
    image:
      "https://images.unsplash.com/photo-1506003094589-53954a26283f?ixlib=rb-4.1.0&auto=format&fit=crop&q=60&w=600",
    description: "Natural ways to keep your skin glowing and healthy.",
    remedies: [
      {
        title: "Aloe Vera Gel",
        details: "Apply fresh aloe vera gel daily to soothe and hydrate your skin.",
      },
      {
        title: "Turmeric Mask",
        details:
          "Mix turmeric with honey and yogurt for glowing, blemish-free skin.",
      },
      {
        title: "Coconut Oil Massage",
        details: "Massage with virgin coconut oil to maintain moisture and softness.",
      },
      
    ],
  },
  {
    title: "Beauty Care",
    image:
      "https://images.unsplash.com/photo-1636499287985-85871c3406fc?ixlib=rb-4.1.0&auto=format&fit=crop&q=60&w=600",
    description: "Enhance your beauty with herbal and organic care tips.",
    remedies: [
      {
        title: "Rose Water Toner",
        details:
          "Use rose water as a natural toner to tighten pores and refresh your skin.",
      },
      {
        title: "Honey & Lemon Face Pack",
        details:
          "Brighten your skin naturally by mixing honey and lemon juice.",
      },
    ],
  },
  {
    title: "Health Care",
    image:
      "https://tse4.mm.bing.net/th/id/OIP.X6l-8Q5xIdOuBbb7I1zkMQHaEO?pid=Api&P=0&h=180",
    description: "Improve your overall health through natural practices.",
    remedies: [
      {
        title: "Ginger Tea",
        details:
          "Drink ginger tea daily to boost immunity and aid digestion.",
      },
      {
        title: "Lemon Water",
        details:
          "Start your morning with lemon water to detoxify your body.",
      },
    ],
  },
  {
    title: "Home Remedies",
    image:
      "https://tse1.mm.bing.net/th/id/OIP.zUKD2bpw7dN4nNjrTSQYYAHaEK?pid=Api&P=0&h=180",
    description:
      "Simple remedies for everyday ailments using household ingredients.",
    remedies: [
      {
        title: "Honey for Cough",
        details:
          "Take a spoonful of honey to soothe sore throats and reduce coughing.",
      },
      {
        title: "Salt Water Gargle",
        details:
          "Gargle with warm salt water to relieve throat irritation.",
      },
    ],
  },
];

export default function Dashboard() {
  const [selectedCategory, setSelectedCategory] = useState(null);

  const handleExplore = (category) => {
    // Toggle same category open/close
    setSelectedCategory(
      selectedCategory === category.title ? null : category.title
    );
  };

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>ðŸŒ¿ Natureâ€™s Cure</h1>
        <p>Explore natural ways to heal and rejuvenate your body and soul.</p>
      </header>

      {/* Category Cards */}
      <div className="card-grid">
        {categories.map((item, index) => (
          <div key={index} className="card">
            <img src={item.image} alt={item.title} className="card-image" />
            <h2>{item.title}</h2>
            <p>{item.description}</p>
            <button className="explore-btn" onClick={() => handleExplore(item)}>
              {selectedCategory === item.title ? "Hide Remedies" : "Explore"}
            </button>

            {/* Remedies Section */}
            {selectedCategory === item.title && (
              <div className="remedies">
                {item.remedies.map((rem, i) => (
                  <div key={i} className="remedy-card">
                    <h3>{rem.title}</h3>
                    <p>{rem.details}</p>
                  </div>
                ))}
              </div>
            )}
          </div>
        ))}
      </div>

    </div>
  );
}
