import React from "react";

function Home() {
  const headingText = "HEALING THROUGH NATURE".split(" ");
  const subText =
    "Discover the power of natural remedies for a healthier lifestyle.".split(
      " "
    );

  return (
    <div className="home-section">
      <div className="overlay">
        <div className="home-content">
          <h1 className="word-animate">
            {headingText.map((word, index) => (
              <span
                key={index}
                style={{ animationDelay: `${index * 0.2}s` }}
                className="fade-word"
              >
                {word}&nbsp;
              </span>
            ))}
          </h1>
          <p className="word-animate">
            {subText.map((word, index) => (
              <span
                key={index}
                style={{ animationDelay: `${index * 0.15}s` }}
                className="fade-word"
              >
                {word}&nbsp;
              </span>
            ))}
          </p>
        </div>
      </div>
    </div>
  );
}

export default Home;
