import React from "react";
import { useNavigate } from "react-router-dom";
import { categories } from "./";

export default function Dashboard() {
  const navigate = useNavigate();

  const handleExplore = (title) => {
    navigate(`/category/${title}`);   // âœ… Now works correctly
  };

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>ðŸŒ¿ Natureâ€™s Cure</h1>
        <p>Explore natural ways to heal and rejuvenate your body and soul.</p>
      </header>

      <div className="card-grid">
        {categories.map((item, index) => (
          <div key={index} className="card">
            <img src={item.image} alt={item.title} className="card-image" />
            <h2>{item.title}</h2>
            <p>{item.description}</p>
            <button
              className="explore-btn"
              onClick={() => handleExplore(item.title)}  // âœ… Correct usage
            >
              Explore
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
