import React, { useState } from 'react'; // We'll create this CSS file next

function LoginPage() {
  // State to hold the values of the input fields
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  // Function to handle form submission
  const handleSubmit = (event) => {
    event.preventDefault(); // Prevents the default browser form submission
    
    // Client-side validation
    if (username.trim() === '' || password.trim() === '') {
      alert('Please enter both username and password.');
      return;
    }

    // *** PLACEHOLDER FOR API CALL / AUTHENTICATION LOGIC ***
    // In a real app, you would send 'username' and 'password' to a backend API
    // using a library like 'fetch' or 'axios' here.
    
    console.log('Attempting login with:', { username, password });
    
    // Example success/failure logic (for demonstration only)
    if (username === 'remedy' && password === 'nature') {
        alert('Login Successful! Welcome to Natural Remedies.');
        // Redirect the user (e.g., using React Router)
    } else {
        alert('Invalid credentials. Please try again.');
    }
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <h1>Login</h1>
        <form onSubmit={handleSubmit}>
          
          <div className="input-group">
            <label htmlFor="username">Username (or Email)</label>
            <input 
              type="text" 
              id="username" 
              value={username} 
              onChange={(e) => setUsername(e.target.value)} 
              required 
            />
          </div>

          <div className="input-group">
            <label htmlFor="password">Password</label>
            <input 
              type="password" 
              id="password" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
              required 
            />
          </div>

          <button type="submit" className="login-button">Log In</button>
          
          <p className="forgot-password"><a href="#">Forgot Password?</a></p>
          <p className="signup-link">
            Don't have an account? <a href="#">Sign Up</a>
          </p>
        </form>
      </div>
    </div>
  );
}

export default LoginPage;
