import React, { usestate } from "react";
import './App.css';
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

import 'bootstrap/dist/css/bootstrap.min.css';
import Home from './Components/Home';
import Header from './Components/Header';
import LoginPage from './Components/Login';
import SignupPage from './Signuppage';
import ForgotPassword from './Forgotpassword';
import About from './Components/About';
import Footer from './Components/Footer';
import Dashboard from './Components/Dashboard';
import Wishlist from "./Components/Wishlist";

function App() {
    const [wishlist, setWishlist] = ([]);
     // Add or remove items from wishlist
  const toggleWishlist = (item) => {
    if (wishlist.some((w) => w.title === item.title)) {
      setWishlist(wishlist.filter((w) => w.title !== item.title));
    } else {
      setWishlist([...wishlist, item]);
    }
  };


  return (
   <Router>
      <div className="App">
        <Header/>
        <Routes>
           <Route path="/" element={<Home />} />
            <Route path="/about" element={<About />} />
          <Route path="/Login" element={<LoginPage />} />
          <Route path="/signup" element={<SignupPage />} />
          <Route path="/forgot" element={<ForgotPassword />} />
          <Route path="/dashboard" element={<Dashboard />} />
 <Route
          path="/"
          element={<Dashboard wishlist={wishlist} toggleWishlist={toggleWishlist} />}
        />
        <Route
          path="/wishlist"
          element={<Wishlist wishlist={wishlist} toggleWishlist={toggleWishlist} />}
        />
        </Routes>
        <Footer/>
      </div>
    </Router>
  );
}


export default App;
